import { useState, useEffect, useCallback } from 'react';
import { AuthService, AuthUser, SessionManager } from '../lib/auth';
import { isSupabaseConfigured } from '../lib/supabase';
import { isSupabaseConfigured } from '../lib/supabase';

interface UseEnhancedAuthReturn {
  user: AuthUser | null;
  loading: boolean;
  error: string | null;
  isAuthenticated: boolean;
  sessionExpiry: Date | null;
  signIn: (email: string, password: string, rememberMe?: boolean) => Promise<void>;
  signUp: (email: string, password: string, fullName?: string, phone?: string) => Promise<void>;
  signOut: () => Promise<void>;
  updateProfile: (updates: Partial<AuthUser>) => Promise<void>;
  resetPassword: (email: string) => Promise<void>;
  refreshSession: () => Promise<void>;
  clearError: () => void;
}

/**
 * Enhanced authentication hook with comprehensive session management
 */
export const useEnhancedAuth = (): UseEnhancedAuthReturn => {
  const [user, setUser] = useState<AuthUser | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [sessionExpiry, setSessionExpiry] = useState<Date | null>(null);

  // Initialize authentication state
  useEffect(() => {
    let mounted = true;

    const initializeAuth = async () => {
      if (!isSupabaseConfigured()) {
        setLoading(false);
        return;
      }

      try {
        // Check if session is valid
        const isValid = await AuthService.isSessionValid();
        if (!isValid) {
          setUser(null);
          setLoading(false);
          return;
        }

        // Get current user
        const currentUser = await AuthService.getCurrentUser();
        if (mounted) {
          setUser(currentUser);

          // Get session expiry
          const expiry = await SessionManager.getSessionExpiry();
          setSessionExpiry(expiry);
        }
      } catch (err) {
        console.error('Auth initialization error:', err);
        if (mounted) {
          setError('Failed to initialize authentication');
        }
      } finally {
        if (mounted) {
          setLoading(false);
        }
      }
    };

    initializeAuth();

    // Set up auth state listener
    const { data: { subscription } } = AuthService.onAuthStateChange(async (authUser) => {
      if (mounted) {
        setUser(authUser);
        setLoading(false);

        if (authUser) {
          const expiry = await SessionManager.getSessionExpiry();
          setSessionExpiry(expiry);
        } else {
          setSessionExpiry(null);
        }
      }
    });

    return () => {
      mounted = false;
      subscription.unsubscribe();
    };
  }, []);

  // Session expiry monitoring
  useEffect(() => {
    if (!sessionExpiry || !user) return;

    const checkExpiry = async () => {
      const isExpiringSoon = await SessionManager.isSessionExpiringSoon();
      if (isExpiringSoon) {
        console.log('Session expiring soon, attempting refresh...');
        await refreshSession();
      }
    };

    // Check every minute
    const interval = setInterval(checkExpiry, 60000);
    return () => clearInterval(interval);
  }, [sessionExpiry, user]);

  /**
   * Sign in user
   */
  const signIn = useCallback(async (email: string, password: string, rememberMe = false) => {
    setLoading(true);
    setError(null);

    try {
      const result = await AuthService.signIn({ email, password, rememberMe });

      if (result.error) {
        setError(result.error.message);
        return;
      }

      if (result.user) {
        setUser(result.user);
        const expiry = await SessionManager.getSessionExpiry();
        setSessionExpiry(expiry);
      }
    } catch (err) {
      setError('An unexpected error occurred during sign in');
      console.error('Sign in error:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Sign up user
   */
  const signUp = useCallback(async (email: string, password: string, fullName?: string, phone?: string) => {
    setLoading(true);
    setError(null);

    try {
      const result = await AuthService.signUp({ email, password, fullName, phone });

      if (result.error) {
        setError(result.error.message);
        return;
      }

      if (result.user) {
        setUser(result.user);
        const expiry = await SessionManager.getSessionExpiry();
        setSessionExpiry(expiry);
      }
    } catch (err) {
      setError('An unexpected error occurred during registration');
      console.error('Sign up error:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Sign out user
   */
  const signOut = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const result = await AuthService.signOut();

      if (result.error) {
        setError(result.error.message);
        return;
      }

      setUser(null);
      setSessionExpiry(null);
      SessionManager.clearSession();
    } catch (err) {
      setError('An unexpected error occurred during sign out');
      console.error('Sign out error:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Update user profile
   */
  const updateProfile = useCallback(async (updates: Partial<AuthUser>) => {
    if (!user) {
      setError('No user logged in');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const result = await AuthService.updateProfile(user.id, updates);

      if (result.error) {
        setError(result.error.message);
        return;
      }

      if (result.user) {
        setUser(result.user);
      }
    } catch (err) {
      setError('Failed to update profile');
      console.error('Update profile error:', err);
    } finally {
      setLoading(false);
    }
  }, [user]);

  /**
   * Reset password
   */
  const resetPassword = useCallback(async (email: string) => {
    setLoading(true);
    setError(null);

    try {
      const result = await AuthService.resetPassword(email);

      if (result.error) {
        setError(result.error.message);
        return;
      }

      // Success message will be handled by the calling component
    } catch (err) {
      setError('Failed to send reset email');
      console.error('Reset password error:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Refresh session
   */
  const refreshSession = useCallback(async () => {
    if (!user) return;

    try {
      const result = await AuthService.refreshSession();

      if (result.error) {
        console.error('Session refresh failed:', result.error);
        // Don't set error state for silent refresh failures
        return;
      }

      // Update session expiry
      const expiry = await SessionManager.getSessionExpiry();
      setSessionExpiry(expiry);
    } catch (err) {
      console.error('Session refresh error:', err);
    }
  }, [user]);

  /**
   * Clear error state
   */
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  return {
    user,
    loading,
    error,
    isAuthenticated: !!user,
    sessionExpiry,
    signIn,
    signUp,
    signOut,
    updateProfile,
    resetPassword,
    refreshSession,
    clearError,
  };
};

