import { useState, useEffect } from 'react';
import { User } from '@supabase/supabase-js';
import { authService, productService, categoryService, userService, reviewService, cartService, wishlistService } from '../services/supabaseService';
import { Product, Category, UserProfile, isSupabaseConfigured } from '../lib/supabase';
import { mockProducts } from '../data/mockData';

// Authentication hook
export const useAuth = () => {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const [profile, setProfile] = useState<UserProfile | null>(null);

  useEffect(() => {
    // If Supabase is not configured, use mock data
    if (!isSupabaseConfigured()) {
      setLoading(false);
      return;
    }

    // Get initial user
    authService.getCurrentUser().then(setUser).finally(() => setLoading(false));

    // Listen for auth changes
    const { data: { subscription } } = authService.onAuthStateChange(async (event, session) => {
      setUser(session?.user ?? null);

      if (session?.user) {
        try {
          const userProfile = await userService.getProfile(session.user.id);
          setProfile(userProfile);
        } catch (error) {
          console.error('Error fetching user profile:', error);
        }
      } else {
        setProfile(null);
      }

      setLoading(false);
    });

    return () => subscription.unsubscribe();
  }, []);

  const signUp = async (email: string, password: string, fullName?: string) => {
    if (!isSupabaseConfigured()) {
      throw new Error('Supabase is not configured. Using mock authentication instead.');
    }

    setLoading(true);
    try {
      const result = await authService.signUp(email, password, fullName);
      return result;
    } finally {
      setLoading(false);
    }
  };

  const signIn = async (email: string, password: string) => {
    if (!isSupabaseConfigured()) {
      throw new Error('Supabase is not configured. Using mock authentication instead.');
    }

    setLoading(true);
    try {
      const result = await authService.signIn(email, password);
      return result;
    } finally {
      setLoading(false);
    }
  };

  const signOut = async () => {
    if (!isSupabaseConfigured()) {
      setUser(null);
      return;
    }

    setLoading(true);
    try {
      await authService.signOut();
    } finally {
      setLoading(false);
    }
  };

  const updateProfile = async (updates: Partial<UserProfile>) => {
    if (!user) throw new Error('No user logged in');

    try {
      const updatedProfile = await userService.updateProfile(user.id, updates);
      setProfile(updatedProfile);
      return updatedProfile;
    } catch (error) {
      console.error('Error updating profile:', error);
      throw error;
    }
  };

  return {
    user,
    profile,
    loading,
    signUp,
    signIn,
    signOut,
    updateProfile,
    isAuthenticated: !!user
  };
};

// Products hook
export const useProducts = (filters?: {
  category?: string;
  featured?: boolean;
  search?: string;
}) => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      setError(null);

      // If Supabase is not configured, use mock data
      if (!isSupabaseConfigured()) {
        // Simulate API delay
        await new Promise(resolve => setTimeout(resolve, 500));

        let filteredData = [...mockProducts];

        // Apply filters to mock data
        if (filters?.category) {
          filteredData = filteredData.filter(p => p.category === filters.category);
        }
        if (filters?.featured !== undefined) {
          filteredData = filteredData.filter(p => p.featured === filters.featured);
        }
        if (filters?.search) {
          const searchLower = filters.search.toLowerCase();
          filteredData = filteredData.filter(p => 
            p.name.toLowerCase().includes(searchLower) ||
            p.description.toLowerCase().includes(searchLower)
          );
        }

        setProducts(filteredData);
        return;
      }

      const data = await productService.getProducts(filters);
      setProducts(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch products');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [filters?.category, filters?.featured, filters?.search]);

  const refetch = () => fetchProducts();

  return { products, loading, error, refetch };
};

// Single product hook
export const useProduct = (id: string) => {
  const [product, setProduct] = useState<Product | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setLoading(true);
        setError(null);

        // If Supabase is not configured, use mock data
        if (!isSupabaseConfigured()) {
          const mockProduct = mockProducts.find(p => p.id === id);
          if (mockProduct) {
            setProduct(mockProduct);
          } else {
            setError('Product not found');
          }
          setLoading(false);
          return;
        }

        const data = await productService.getProduct(id);
        setProduct(data);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to fetch product');
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      fetchProduct();
    }
  }, [id]);

  return { product, loading, error };
};

// Categories hook
export const useCategories = () => {
  const [categories, setCategories] = useState<Category[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        setLoading(true);
        setError(null);

        // If Supabase is not configured, use mock data
        if (!isSupabaseConfigured()) {
          const mockCategories = [
            { id: '1', name: 'Furniture', created_at: new Date().toISOString() },
            { id: '2', name: 'Lighting', created_at: new Date().toISOString() },
            { id: '3', name: 'Decor', created_at: new Date().toISOString() },
          ];
          setCategories(mockCategories);
          setLoading(false);
          return;
        }

        const data = await categoryService.getCategories();
        setCategories(data);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to fetch categories');
      } finally {
        setLoading(false);
      }
    };

    fetchCategories();
  }, []);

  return { categories, loading, error };
};

// Reviews hook
export const useReviews = (productId: string) => {
  const [reviews, setReviews] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        setLoading(true);
        setError(null);

        if (!isSupabaseConfigured()) {
          // Mock reviews data
          const mockReviews = [
            {
              id: '1',
              rating: 5,
              comment: 'Excellent product! Highly recommended.',
              created_at: new Date().toISOString(),
              user_profiles: { full_name: 'John Doe', avatar_url: null }
            },
            {
              id: '2',
              rating: 4,
              comment: 'Good quality, fast delivery.',
              created_at: new Date().toISOString(),
              user_profiles: { full_name: 'Jane Smith', avatar_url: null }
            }
          ];
          setReviews(mockReviews);
          setLoading(false);
          return;
        }

        const data = await reviewService.getProductReviews(productId);
        setReviews(data);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to fetch reviews');
      } finally {
        setLoading(false);
      }
    };

    if (productId) {
      fetchReviews();
    }
  }, [productId]);

  const addReview = async (rating: number, comment: string) => {
    if (!isSupabaseConfigured()) {
      throw new Error('Supabase is not configured');
    }

    const user = await authService.getCurrentUser();
    if (!user) throw new Error('User not authenticated');

    const review = await reviewService.createReview({
      product_id: productId,
      user_id: user.id,
      rating,
      comment
    });

    // Refresh reviews
    const updatedReviews = await reviewService.getProductReviews(productId);
    setReviews(updatedReviews);

    return review;
  };

  return { reviews, loading, error, addReview };
};

// Cart hook
export const useCart = () => {
  const [cartItems, setCartItems] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { user } = useAuth();

  const fetchCartItems = async () => {
    if (!user) {
      setCartItems([]);
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      if (!isSupabaseConfigured()) {
        setCartItems([]);
        setLoading(false);
        return;
      }

      const data = await cartService.getCartItems(user.id);
      setCartItems(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch cart items');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCartItems();
  }, [user]);

  const addToCart = async (productId: string, quantity: number = 1) => {
    if (!user) throw new Error('User not authenticated');
    if (!isSupabaseConfigured()) throw new Error('Supabase not configured');

    await cartService.addToCart(user.id, productId, quantity);
    await fetchCartItems();
  };

  const updateQuantity = async (cartItemId: string, quantity: number) => {
    if (!isSupabaseConfigured()) throw new Error('Supabase not configured');

    await cartService.updateCartItem(cartItemId, quantity);
    await fetchCartItems();
  };

  const removeFromCart = async (cartItemId: string) => {
    if (!isSupabaseConfigured()) throw new Error('Supabase not configured');

    await cartService.removeFromCart(cartItemId);
    await fetchCartItems();
  };

  const clearCart = async () => {
    if (!user || !isSupabaseConfigured()) return;

    await cartService.clearCart(user.id);
    await fetchCartItems();
  };

  return {
    cartItems,
    loading,
    error,
    addToCart,
    updateQuantity,
    removeFromCart,
    clearCart,
    refetch: fetchCartItems
  };
};

// Wishlist hook
export const useWishlist = () => {
  const [wishlistItems, setWishlistItems] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { user } = useAuth();

  const fetchWishlistItems = async () => {
    if (!user) {
      setWishlistItems([]);
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      if (!isSupabaseConfigured()) {
        setWishlistItems([]);
        setLoading(false);
        return;
      }

      const data = await wishlistService.getWishlistItems(user.id);
      setWishlistItems(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch wishlist items');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWishlistItems();
  }, [user]);

  const addToWishlist = async (productId: string) => {
    if (!user) throw new Error('User not authenticated');
    if (!isSupabaseConfigured()) throw new Error('Supabase not configured');

    await wishlistService.addToWishlist(user.id, productId);
    await fetchWishlistItems();
  };

  const removeFromWishlist = async (productId: string) => {
    if (!user || !isSupabaseConfigured()) return;

    await wishlistService.removeFromWishlist(user.id, productId);
    await fetchWishlistItems();
  };

  return {
    wishlistItems,
    loading,
    error,
    addToWishlist,
    removeFromWishlist,
    refetch: fetchWishlistItems
  };
};

