import { supabase, isSupabaseConfigured } from './supabase';
import { User } from '@supabase/supabase-js';

export interface AuthUser {
  id: string;
  email: string;
  full_name?: string;
  avatar_url?: string;
  phone?: string;
  email_verified?: boolean;
  last_login?: string;
  created_at: string;
  updated_at: string;
}

export interface AuthError {
  message: string;
  status?: number;
  code?: string;
}

export interface AuthResponse {
  user: AuthUser | null;
  error: AuthError | null;
}

export interface SignUpData {
  email: string;
  password: string;
  fullName?: string;
  phone?: string;
}

export interface SignInData {
  email: string;
  password: string;
  rememberMe?: boolean;
}

/**
 * Enhanced Authentication Service with comprehensive error handling
 */
export class AuthService {
  /**
   * Sign up a new user
   */
  static async signUp(data: SignUpData): Promise<AuthResponse> {
    if (!isSupabaseConfigured()) {
      return {
        user: null,
        error: { message: 'Supabase is not configured. Please set up your environment variables.' }
      };
    }

    try {
      const { data: authData, error: authError } = await supabase!.auth.signUp({
        email: data.email,
        password: data.password,
        options: {
          data: {
            full_name: data.fullName,
            phone: data.phone,
          }
        }
      });

      if (authError) {
        return {
          user: null,
          error: {
            message: this.getReadableErrorMessage(authError.message),
            code: authError.message
          }
        };
      }

      if (!authData.user) {
        return {
          user: null,
          error: { message: 'Registration failed. Please try again.' }
        };
      }

      // Get the created profile
      const profile = await this.getUserProfile(authData.user.id);

      return {
        user: profile,
        error: null
      };
    } catch (error) {
      return {
        user: null,
        error: {
          message: error instanceof Error ? error.message : 'An unexpected error occurred during registration.'
        }
      };
    }
  }

  /**
   * Sign in an existing user
   */
  static async signIn(data: {
    email: string;
    password: string;
    rememberMe?: boolean;
  }): Promise<AuthResponse> {
    if (!isSupabaseConfigured()) {
      return {
        user: null,
        error: { message: 'Supabase is not configured. Please set up your environment variables.' }
      };
    }

    try {
      const { data: authData, error: authError } = await supabase!.auth.signInWithPassword({
        email: data.email,
        password: data.password,
      });

      if (authError) {
        return {
          user: null,
          error: {
            message: this.getReadableErrorMessage(authError.message),
            code: authError.message
          }
        };
      }

      if (!authData.user) {
        return {
          user: null,
          error: { message: 'Login failed. Please check your credentials.' }
        };
      }

      // Get user profile
      const profile = await this.getUserProfile(authData.user.id);

      // Handle "Remember Me" functionality
      if (data.rememberMe) {
        localStorage.setItem('supabase_remember_me', 'true');
      } else {
        localStorage.removeItem('supabase_remember_me');
      }

      return {
        user: profile,
        error: null
      };
    } catch (error) {
      return {
        user: null,
        error: {
          message: error instanceof Error ? error.message : 'An unexpected error occurred during login.'
        }
      };
    }
  }

  /**
   * Sign out the current user
   */
  static async signOut(): Promise<{ error: AuthError | null }> {
    if (!isSupabaseConfigured()) {
      return { error: null };
    }

    try {
      const { error } = await supabase!.auth.signOut();

      // Clear remember me preference
      localStorage.removeItem('supabase_remember_me');

      if (error) {
        return {
          error: {
            message: this.getReadableErrorMessage(error.message),
            code: error.message
          }
        };
      }

      return { error: null };
    } catch (error) {
      return {
        error: {
          message: error instanceof Error ? error.message : 'An unexpected error occurred during logout.'
        }
      };
    }
  }

  /**
   * Get current authenticated user
   */
  static async getCurrentUser(): Promise<AuthUser | null> {
    if (!isSupabaseConfigured()) {
      return null;
    }

    try {
      const { data: { user }, error } = await supabase!.auth.getUser();

      if (error || !user) {
        return null;
      }

      return await this.getUserProfile(user.id);
    } catch (error) {
      console.error('Error getting current user:', error);
      return null;
    }
  }

  /**
   * Get user profile from database
   */
  static async getUserProfile(userId: string): Promise<AuthUser | null> {
    if (!isSupabaseConfigured()) {
      return null;
    }

    try {
      const { data, error } = await supabase!
        .from('profiles')
        .select('*')
        .eq('id', userId)
        .single();

      if (error || !data) {
        return null;
      }

      return {
        id: data.id,
        email: data.email,
        full_name: data.full_name,
        avatar_url: data.avatar_url,
        phone: data.phone,
        email_verified: data.email_verified,
        last_login: data.last_login,
        created_at: data.created_at,
        updated_at: data.updated_at,
      };
    } catch (error) {
      console.error('Error fetching user profile:', error);
      return null;
    }
  }

  /**
   * Update user profile
   */
  static async updateProfile(userId: string, updates: Partial<AuthUser>): Promise<AuthResponse> {
    if (!isSupabaseConfigured()) {
      return {
        user: null,
        error: { message: 'Supabase is not configured.' }
      };
    }

    try {
      const { data, error } = await supabase!
        .from('profiles')
        .update(updates)
        .eq('id', userId)
        .select()
        .single();

      if (error) {
        return {
          user: null,
          error: {
            message: this.getReadableErrorMessage(error.message),
            code: error.code
          }
        };
      }

      return {
        user: data as AuthUser,
        error: null
      };
    } catch (error) {
      return {
        user: null,
        error: {
          message: error instanceof Error ? error.message : 'Failed to update profile.'
        }
      };
    }
  }

  /**
   * Reset password
   */
  static async resetPassword(email: string): Promise<{ error: AuthError | null }> {
    if (!isSupabaseConfigured()) {
      return {
        error: { message: 'Supabase is not configured.' }
      };
    }

    try {
      const { error } = await supabase!.auth.resetPasswordForEmail(email, {
        redirectTo: `${window.location.origin}/reset-password`,
      });

      if (error) {
        return {
          error: {
            message: this.getReadableErrorMessage(error.message),
            code: error.message
          }
        };
      }

      return { error: null };
    } catch (error) {
      return {
        error: {
          message: error instanceof Error ? error.message : 'Failed to send reset email.'
        }
      };
    }
  }

  /**
   * Update password
   */
  static async updatePassword(newPassword: string): Promise<{ error: AuthError | null }> {
    if (!isSupabaseConfigured()) {
      return {
        error: { message: 'Supabase is not configured.' }
      };
    }

    try {
      const { error } = await supabase!.auth.updateUser({
        password: newPassword
      });

      if (error) {
        return {
          error: {
            message: this.getReadableErrorMessage(error.message),
            code: error.message
          }
        };
      }

      return { error: null };
    } catch (error) {
      return {
        error: {
          message: error instanceof Error ? error.message : 'Failed to update password.'
        }
      };
    }
  }

  /**
   * Listen to authentication state changes
   */
  static onAuthStateChange(callback: (user: AuthUser | null) => void) {
    if (!isSupabaseConfigured()) {
      return { data: { subscription: { unsubscribe: () => {} } } };
    }

    return supabase!.auth.onAuthStateChange(async (event, session) => {
      if (session?.user) {
        const profile = await this.getUserProfile(session.user.id);
        callback(profile);
      } else {
        callback(null);
      }
    });
  }

  /**
   * Check if user session is valid
   */
  static async isSessionValid(): Promise<boolean> {
    if (!isSupabaseConfigured()) {
      return false;
    }

    try {
      const { data: { session }, error } = await supabase!.auth.getSession();
      return !error && !!session;
    } catch {
      return false;
    }
  }

  /**
   * Refresh the current session
   */
  static async refreshSession(): Promise<{ error: AuthError | null }> {
    if (!isSupabaseConfigured()) {
      return { error: { message: 'Supabase is not configured.' } };
    }

    try {
      const { error } = await supabase!.auth.refreshSession();

      if (error) {
        return {
          error: {
            message: this.getReadableErrorMessage(error.message),
            code: error.message
          }
        };
      }

      return { error: null };
    } catch (error) {
      return {
        error: {
          message: error instanceof Error ? error.message : 'Failed to refresh session.'
        }
      };
    }
  }

  /**
   * Convert technical error messages to user-friendly ones
   */
  private static getReadableErrorMessage(errorMessage: string): string {
    const errorMap: Record<string, string> = {
      'Invalid login credentials': 'Invalid email or password. Please check your credentials and try again.',
      'Email not confirmed': 'Please check your email and click the confirmation link before signing in.',
      'User already registered': 'An account with this email already exists. Please sign in instead.',
      'Password should be at least 6 characters': 'Password must be at least 6 characters long.',
      'Unable to validate email address: invalid format': 'Please enter a valid email address.',
      'signup_disabled': 'New registrations are currently disabled. Please contact support.',
      'email_address_invalid': 'Please enter a valid email address.',
      'password_too_short': 'Password must be at least 6 characters long.',
      'weak_password': 'Please choose a stronger password with a mix of letters, numbers, and symbols.',
      'email_address_not_authorized': 'This email address is not authorized to create an account.',
      'too_many_requests': 'Too many requests. Please wait a moment before trying again.',
    };

    // Check for exact matches first
    if (errorMap[errorMessage]) {
      return errorMap[errorMessage];
    }

    // Check for partial matches
    for (const [key, value] of Object.entries(errorMap)) {
      if (errorMessage.toLowerCase().includes(key.toLowerCase())) {
        return value;
      }
    }

    // Return original message if no match found
    return errorMessage;
  }
}

/**
 * Authentication validation utilities
 */
export class AuthValidation {
  /**
   * Validate email format
   */
  static isValidEmail(email: string): boolean {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  }

  /**
   * Validate password strength
   */
  static validatePassword(password: string): {
    isValid: boolean;
    errors: string[];
    strength: 'weak' | 'medium' | 'strong';
  } {
    const errors: string[] = [];
    let strength: 'weak' | 'medium' | 'strong' = 'weak';

    if (password.length < 6) {
      errors.push('Password must be at least 6 characters long');
    }

    if (password.length < 8) {
      errors.push('For better security, use at least 8 characters');
    }

    if (!/[a-z]/.test(password)) {
      errors.push('Include at least one lowercase letter');
    }

    if (!/[A-Z]/.test(password)) {
      errors.push('Include at least one uppercase letter');
    }

    if (!/\d/.test(password)) {
      errors.push('Include at least one number');
    }

    if (!/[!@#$%^&*(),.?":{}|<>]/.test(password)) {
      errors.push('Include at least one special character');
    }

    // Determine strength
    if (errors.length === 0) {
      strength = 'strong';
    } else if (errors.length <= 2) {
      strength = 'medium';
    }

    return {
      isValid: password.length >= 6,
      errors,
      strength
    };
  }

  /**
   * Check if password meets minimum requirements
   */
  static meetsMinimumRequirements(password: string): boolean {
    return password.length >= 6;
  }
}

/**
 * Session management utilities
 */
export class SessionManager {
  private static readonly SESSION_KEY = 'supabase_session';
  private static readonly REMEMBER_KEY = 'supabase_remember_me';

  /**
   * Check if user wants to be remembered
   */
  static shouldRememberUser(): boolean {
    return localStorage.getItem(this.REMEMBER_KEY) === 'true';
  }

  /**
   * Set remember me preference
   */
  static setRememberMe(remember: boolean): void {
    if (remember) {
      localStorage.setItem(this.REMEMBER_KEY, 'true');
    } else {
      localStorage.removeItem(this.REMEMBER_KEY);
    }
  }

  /**
   * Clear all session data
   */
  static clearSession(): void {
    localStorage.removeItem(this.SESSION_KEY);
    localStorage.removeItem(this.REMEMBER_KEY);
  }

  /**
   * Get session expiry time
   */
  static async getSessionExpiry(): Promise<Date | null> {
    if (!isSupabaseConfigured()) return null;

    try {
      const { data: { session } } = await supabase!.auth.getSession();
      return session?.expires_at ? new Date(session.expires_at * 1000) : null;
    } catch {
      return null;
    }
  }

  /**
   * Check if session is about to expire (within 5 minutes)
   */
  static async isSessionExpiringSoon(): Promise<boolean> {
    const expiry = await this.getSessionExpiry();
    if (!expiry) return false;

    const fiveMinutesFromNow = new Date(Date.now() + 5 * 60 * 1000);
    return expiry < fiveMinutesFromNow;
  }
}

