import { supabase, Product, Category, UserProfile, Order, OrderItem, Review, CartItem, WishlistItem } from '../lib/supabase';

// Helper function to check if operation should proceed
const checkSupabaseConnection = () => {
  if (!supabase) {
    throw new Error('Supabase is not configured. Please set up your environment variables.');
  }
};

// Authentication Services
export const authService = {
  // Sign up with email and password
  async signUp(email: string, password: string, fullName?: string) {
    checkSupabaseConnection();
    const { data, error } = await supabase.auth.signUp({
      email,
      password,
      options: {
        data: {
          full_name: fullName,
        }
      }
    });

    if (error) throw error;
    return data;
  },

  // Sign in with email and password
  async signIn(email: string, password: string) {
    checkSupabaseConnection();
    const { data, error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (error) throw error;
    return data;
  },

  // Sign out
  async signOut() {
    checkSupabaseConnection();
    const { error } = await supabase.auth.signOut();
    if (error) throw error;
  },

  // Get current user
  async getCurrentUser() {
    checkSupabaseConnection();
    const { data: { user }, error } = await supabase.auth.getUser();
    if (error) throw error;
    return user;
  },

  // Reset password
  async resetPassword(email: string) {
    checkSupabaseConnection();
    const { error } = await supabase.auth.resetPasswordForEmail(email);
    if (error) throw error;
  },

  // Listen to auth changes
  onAuthStateChange(callback: (event: string, session: any) => void) {
    checkSupabaseConnection();
    return supabase.auth.onAuthStateChange(callback);
  }
};

// Product Services
export const productService = {
  // Get all products
  async getProducts(filters?: {
    category?: string;
    featured?: boolean;
    search?: string;
    limit?: number;
    offset?: number;
  }) {
    checkSupabaseConnection();
    let query = supabase
      .from('products')
      .select('*')
      .order('created_at', { ascending: false });

    if (filters?.category) {
      query = query.eq('category', filters.category);
    }

    if (filters?.featured !== undefined) {
      query = query.eq('featured', filters.featured);
    }

    if (filters?.search) {
      query = query.or(`name.ilike.%${filters.search}%,description.ilike.%${filters.search}%`);
    }

    if (filters?.limit) {
      query = query.limit(filters.limit);
    }

    if (filters?.offset) {
      query = query.range(filters.offset, filters.offset + (filters.limit || 10) - 1);
    }

    const { data, error } = await query;
    if (error) throw error;
    return data as Product[];
  },

  // Get single product
  async getProduct(id: string) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('products')
      .select('*')
      .eq('id', id)
      .single();

    if (error) throw error;
    return data as Product;
  },

  // Create product (admin only)
  async createProduct(product: Omit<Product, 'id' | 'created_at' | 'updated_at'>) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('products')
      .insert([product])
      .select()
      .single();

    if (error) throw error;
    return data as Product;
  },

  // Update product (admin only)
  async updateProduct(id: string, updates: Partial<Product>) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('products')
      .update(updates)
      .eq('id', id)
      .select()
      .single();

    if (error) throw error;
    return data as Product;
  },

  // Delete product (admin only)
  async deleteProduct(id: string) {
    checkSupabaseConnection();
    const { error } = await supabase
      .from('products')
      .delete()
      .eq('id', id);

    if (error) throw error;
  },

  // Update stock
  async updateStock(id: string, quantity: number) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('products')
      .update({ stock: quantity })
      .eq('id', id)
      .select()
      .single();

    if (error) throw error;
    return data as Product;
  }
};

// Category Services
export const categoryService = {
  // Get all categories
  async getCategories() {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('categories')
      .select('*')
      .order('name');

    if (error) throw error;
    return data as Category[];
  },

  // Create category
  async createCategory(category: Omit<Category, 'id' | 'created_at'>) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('categories')
      .insert([category])
      .select()
      .single();

    if (error) throw error;
    return data as Category;
  }
};

// User Profile Services
export const userService = {
  // Get user profile
  async getProfile(userId: string) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('user_profiles')
      .select('*')
      .eq('id', userId)
      .single();

    if (error) throw error;
    return data as UserProfile;
  },

  // Update user profile
  async updateProfile(userId: string, updates: Partial<UserProfile>) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('user_profiles')
      .update(updates)
      .eq('id', userId)
      .select()
      .single();

    if (error) throw error;
    return data as UserProfile;
  },

  // Create user profile
  async createProfile(profile: Omit<UserProfile, 'created_at' | 'updated_at'>) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('user_profiles')
      .insert([profile])
      .select()
      .single();

    if (error) throw error;
    return data as UserProfile;
  }
};

// Order Services
export const orderService = {
  // Create order
  async createOrder(order: Omit<Order, 'id' | 'created_at'>, items: Omit<OrderItem, 'id' | 'order_id'>[]) {
    // Start transaction
    const { data: orderData, error: orderError } = await supabase
      .from('orders')
      .insert([order])
      .select()
      .single();

    if (orderError) throw orderError;

    // Add order items
    const orderItems = items.map(item => ({
      ...item,
      order_id: orderData.id
    }));

    const { data: itemsData, error: itemsError } = await supabase
      .from('order_items')
      .insert(orderItems)
      .select();

    if (itemsError) throw itemsError;

    return { order: orderData as Order, items: itemsData as OrderItem[] };
  },

  // Get user orders
  async getUserOrders(userId: string) {
    const { data, error } = await supabase
      .from('orders')
      .select(`
        *,
        order_items (
          *,
          product:products (*)
        )
      `)
      .eq('user_id', userId)
      .order('created_at', { ascending: false });

    if (error) throw error;
    return data;
  },

  // Get single order
  async getOrder(orderId: string) {
    const { data, error } = await supabase
      .from('orders')
      .select(`
        *,
        order_items (
          *,
          product:products (*)
        )
      `)
      .eq('id', orderId)
      .single();

    if (error) throw error;
    return data;
  },

  // Update order status
  async updateOrderStatus(orderId: string, status: string) {
    const { data, error } = await supabase
      .from('orders')
      .update({ status })
      .eq('id', orderId)
      .select()
      .single();

    if (error) throw error;
    return data as Order;
  }
};

// Review Services
export const reviewService = {
  // Get reviews for a product
  async getProductReviews(productId: string) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('reviews')
      .select(`
        *,
        user_profiles (
          full_name,
          avatar_url
        )
      `)
      .eq('product_id', productId)
      .order('created_at', { ascending: false });

    if (error) throw error;
    return data;
  },

  // Create review
  async createReview(review: Omit<Review, 'id' | 'created_at'>) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('reviews')
      .insert([review])
      .select()
      .single();

    if (error) throw error;
    return data as Review;
  },

  // Update review
  async updateReview(reviewId: string, updates: Partial<Review>) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('reviews')
      .update(updates)
      .eq('id', reviewId)
      .select()
      .single();

    if (error) throw error;
    return data as Review;
  },

  // Delete review
  async deleteReview(reviewId: string) {
    checkSupabaseConnection();
    const { error } = await supabase
      .from('reviews')
      .delete()
      .eq('id', reviewId);

    if (error) throw error;
  }
};

// Cart Services
export const cartService = {
  // Get user's cart items
  async getCartItems(userId: string) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('cart_items')
      .select(`
        *,
        products (*)
      `)
      .eq('user_id', userId);

    if (error) throw error;
    return data;
  },

  // Add item to cart
  async addToCart(userId: string, productId: string, quantity: number = 1) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('cart_items')
      .upsert({
        user_id: userId,
        product_id: productId,
        quantity
      })
      .select()
      .single();

    if (error) throw error;
    return data as CartItem;
  },

  // Update cart item quantity
  async updateCartItem(cartItemId: string, quantity: number) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('cart_items')
      .update({ quantity })
      .eq('id', cartItemId)
      .select()
      .single();

    if (error) throw error;
    return data as CartItem;
  },

  // Remove item from cart
  async removeFromCart(cartItemId: string) {
    checkSupabaseConnection();
    const { error } = await supabase
      .from('cart_items')
      .delete()
      .eq('id', cartItemId);

    if (error) throw error;
  },

  // Clear user's cart
  async clearCart(userId: string) {
    checkSupabaseConnection();
    const { error } = await supabase
      .from('cart_items')
      .delete()
      .eq('user_id', userId);

    if (error) throw error;
  }
};

// Wishlist Services
export const wishlistService = {
  // Get user's wishlist items
  async getWishlistItems(userId: string) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('wishlist_items')
      .select(`
        *,
        products (*)
      `)
      .eq('user_id', userId);

    if (error) throw error;
    return data;
  },

  // Add item to wishlist
  async addToWishlist(userId: string, productId: string) {
    checkSupabaseConnection();
    const { data, error } = await supabase
      .from('wishlist_items')
      .insert({
        user_id: userId,
        product_id: productId
      })
      .select()
      .single();

    if (error) throw error;
    return data as WishlistItem;
  },

  // Remove item from wishlist
  async removeFromWishlist(userId: string, productId: string) {
    checkSupabaseConnection();
    const { error } = await supabase
      .from('wishlist_items')
      .delete()
      .eq('user_id', userId)
      .eq('product_id', productId);

    if (error) throw error;
  }
};

