import { AuthUser } from '../lib/auth';

/**
 * Authentication utility functions
 */

/**
 * Check if user has specific role or permission
 */
export const hasRole = (user: AuthUser | null, role: string): boolean => {
  if (!user) return false;
  // This would typically check user.roles or user.permissions
  // For now, we'll use a simple check
  return user.email?.includes('admin') || false;
};

/**
 * Check if user can access admin features
 */
export const isAdmin = (user: AuthUser | null): boolean => {
  return hasRole(user, 'admin');
};

/**
 * Get user display name
 */
export const getUserDisplayName = (user: AuthUser | null): string => {
  if (!user) return 'Guest';
  return user.full_name || user.email?.split('@')[0] || 'User';
};

/**
 * Get user initials for avatar
 */
export const getUserInitials = (user: AuthUser | null): string => {
  if (!user) return 'G';

  if (user.full_name) {
    return user.full_name
      .split(' ')
      .map(name => name[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  }

  return user.email?.[0]?.toUpperCase() || 'U';
};

/**
 * Format last login time
 */
export const formatLastLogin = (lastLogin: string | null): string => {
  if (!lastLogin) return 'Never';

  const date = new Date(lastLogin);
  const now = new Date();
  const diffInHours = (now.getTime() - date.getTime()) / (1000 * 60 * 60);

  if (diffInHours < 1) {
    return 'Just now';
  } else if (diffInHours < 24) {
    return `${Math.floor(diffInHours)} hours ago`;
  } else if (diffInHours < 168) { // 7 days
    return `${Math.floor(diffInHours / 24)} days ago`;
  } else {
    return date.toLocaleDateString();
  }
};

/**
 * Check if account is recently created (within last 7 days)
 */
export const isNewAccount = (user: AuthUser | null): boolean => {
  if (!user) return false;

  const createdAt = new Date(user.created_at);
  const sevenDaysAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);

  return createdAt > sevenDaysAgo;
};

/**
 * Generate secure avatar URL
 */
export const getSecureAvatarUrl = (user: AuthUser | null): string => {
  if (!user) return '';

  if (user.avatar_url) {
    return user.avatar_url;
  }

  // Generate Gravatar URL as fallback
  const email = user.email?.toLowerCase().trim() || '';
  const hash = btoa(email); // Simple hash for demo - use proper hash in production
  return `https://www.gravatar.com/avatar/${hash}?d=identicon&s=200`;
};

/**
 * Validate session expiry
 */
export const isSessionExpired = (expiry: Date | null): boolean => {
  if (!expiry) return true;
  return new Date() > expiry;
};

/**
 * Calculate session time remaining
 */
export const getSessionTimeRemaining = (expiry: Date | null): number => {
  if (!expiry) return 0;
  const remaining = expiry.getTime() - Date.now();
  return Math.max(0, remaining);
};

/**
 * Format session time remaining
 */
export const formatSessionTimeRemaining = (expiry: Date | null): string => {
  const remaining = getSessionTimeRemaining(expiry);

  if (remaining === 0) return 'Expired';

  const hours = Math.floor(remaining / (1000 * 60 * 60));
  const minutes = Math.floor((remaining % (1000 * 60 * 60)) / (1000 * 60));

  if (hours > 0) {
    return `${hours}h ${minutes}m`;
  } else {
    return `${minutes}m`;
  }
};

/**
 * Authentication route guards
 */
export const authGuards = {
  /**
   * Require authentication
   */
  requireAuth: (user: AuthUser | null): boolean => {
    return !!user;
  },

  /**
   * Require email verification
   */
  requireEmailVerification: (user: AuthUser | null): boolean => {
    return !!user?.email_verified;
  },

  /**
   * Require admin role
   */
  requireAdmin: (user: AuthUser | null): boolean => {
    return isAdmin(user);
  },

  /**
   * Require complete profile
   */
  requireCompleteProfile: (user: AuthUser | null): boolean => {
    return !!(user?.full_name && user?.phone);
  },
};

/**
 * Authentication error messages
 */
export const authErrorMessages = {
  INVALID_CREDENTIALS: 'Invalid email or password. Please check your credentials.',
  EMAIL_NOT_CONFIRMED: 'Please check your email and confirm your account.',
  USER_NOT_FOUND: 'No account found with this email address.',
  EMAIL_ALREADY_EXISTS: 'An account with this email already exists.',
  WEAK_PASSWORD: 'Password is too weak. Please choose a stronger password.',
  NETWORK_ERROR: 'Network error. Please check your connection and try again.',
  SESSION_EXPIRED: 'Your session has expired. Please sign in again.',
  UNAUTHORIZED: 'You are not authorized to access this resource.',
  EMAIL_VERIFICATION_REQUIRED: 'Email verification is required to access this feature.',
};

/**
 * Local storage keys for auth data
 */
export const authStorageKeys = {
  REMEMBER_ME: 'supabase_remember_me',
  REMEMBERED_EMAIL: 'supabase_remembered_email',
  SESSION_DATA: 'supabase_session',
  USER_PREFERENCES: 'user_preferences',
} as const;

client/src/utils/mockAuth.ts.content:
// Mock authentication storage and validation
interface StoredUser {
  id: string;
  email: string;
  password: string; // In real app, this would be hashed
  fullName: string;
  mobileNumber?: string;
  avatar?: string;
  createdAt: string;
}

const USERS_STORAGE_KEY = 'interoo_users';
const CURRENT_USER_KEY = 'interoo_current_user';

export class MockAuthService {
  // Get all stored users
  private static getStoredUsers(): StoredUser[] {
    try {
      const users = localStorage.getItem(USERS_STORAGE_KEY);
      return users ? JSON.parse(users) : [];
    } catch {
      return [];
    }
  }

  // Save users to storage
  private static saveUsers(users: StoredUser[]): void {
    try {
      localStorage.setItem(USERS_STORAGE_KEY, JSON.stringify(users));
    } catch (error) {
      console.error('Failed to save users:', error);
    }
  }

  // Register a new user
  static async register(email: string, password: string, fullName: string, mobileNumber?: string): Promise<StoredUser> {
    const users = this.getStoredUsers();

    // Check if user already exists
    const existingUser = users.find(user => user.email.toLowerCase() === email.toLowerCase());
    if (existingUser) {
      throw new Error('User with this email already exists');
    }

    // Create new user
    const newUser: StoredUser = {
      id: `user_${Date.now()}_${Math.random().toString(36).substring(2, 8)}`,
      email: email.toLowerCase(),
      password, // In real app, this would be hashed
      fullName,
      mobileNumber,
      avatar: 'https://images.pexels.com/photos/1043471/pexels-photo-1043471.jpeg?auto=compress&cs=tinysrgb&w=100',
      createdAt: new Date().toISOString(),
    };

    // Save user
    users.push(newUser);
    this.saveUsers(users);

    // Auto-login after registration
    this.setCurrentUser(newUser);

    return newUser;
  }

  // Login user
  static async login(email: string, password: string): Promise<StoredUser> {
    const users = this.getStoredUsers();

    // Find user by email
    const user = users.find(user => 
      user.email.toLowerCase() === email.toLowerCase() && 
      user.password === password
    );

    if (!user) {
      throw new Error('Invalid email or password');
    }

    // Set as current user
    this.setCurrentUser(user);

    return user;
  }

  // Sign in (alias for login)
  static async signIn(email: string, password: string): Promise<any> {
    const user = await this.login(email, password);
    return {
      id: user.id,
      email: user.email,
      fullName: user.fullName,
      phone: user.mobileNumber,
      avatarUrl: user.avatar,
      emailVerified: true,
      lastLogin: new Date().toISOString(),
      createdAt: new Date(user.createdAt).toISOString(),
      updatedAt: new Date().toISOString(),
      password: user.password,
      address: null
    };
  }

  // Sign up
  static async signUp(userData: { email: string; password: string; fullName: string; phone?: string }): Promise<any> {
    const user = await this.register(userData.email, userData.password, userData.fullName, userData.phone);
    return {
      id: user.id,
      email: user.email,
      fullName: user.fullName,
      phone: userData.phone,
      avatarUrl: user.avatar,
      emailVerified: true,
      lastLogin: new Date().toISOString(),
      createdAt: new Date(user.createdAt).toISOString(),
      updatedAt: new Date().toISOString(),
      password: user.password,
      address: null
    };
  }

  // Update user profile
  static async updateProfile(userId: string, updateData: Partial<{
    fullName: string;
    phone: string;
    address: any;
  }>): Promise<any> {
    const users = this.getStoredUsers();
    const userIndex = users.findIndex(user => user.id === userId);

    if (userIndex === -1) {
      throw new Error('User not found');
    }

    // Update the stored user
    if (updateData.fullName) users[userIndex].fullName = updateData.fullName;
    if (updateData.phone) users[userIndex].mobileNumber = updateData.phone;

    this.saveUsers(users);

    // Update current user session
    const currentUser = this.getCurrentUser();
    if (currentUser && currentUser.id === userId) {
      const updatedUser = {
        ...currentUser,
        name: updateData.fullName || currentUser.name,
        mobileNumber: updateData.phone || currentUser.mobileNumber,
      };
      localStorage.setItem(CURRENT_USER_KEY, JSON.stringify(updatedUser));
    }

    // Return updated user in the expected format
    const updatedUser = users[userIndex];
    return {
      id: updatedUser.id,
      email: updatedUser.email,
      fullName: updatedUser.fullName,
      phone: updatedUser.mobileNumber,
      avatarUrl: updatedUser.avatar,
      emailVerified: true,
      lastLogin: new Date().toISOString(),
      createdAt: new Date(updatedUser.createdAt).toISOString(),
      updatedAt: new Date().toISOString(),
      password: updatedUser.password,
      address: updateData.address || null
    };
  }

  // Set current user
  private static setCurrentUser(user: StoredUser): void {
    try {
      const userForStorage = {
        id: user.id,
        email: user.email,
        name: user.fullName,
        avatar: user.avatar,
        mobileNumber: user.mobileNumber,
      };
      localStorage.setItem(CURRENT_USER_KEY, JSON.stringify(userForStorage));
    } catch (error) {
      console.error('Failed to save current user:', error);
    }
  }

  // Get current user
  static getCurrentUser(): any | null {
    try {
      const user = localStorage.getItem(CURRENT_USER_KEY);
      return user ? JSON.parse(user) : null;
    } catch {
      return null;
    }
  }

  // Logout user
  static logout(): void {
    try {
      localStorage.removeItem(CURRENT_USER_KEY);
    } catch (error) {
      console.error('Failed to logout:', error);
    }
  }

  // Check if user exists
  static userExists(email: string): boolean {
    const users = this.getStoredUsers();
    return users.some(user => user.email.toLowerCase() === email.toLowerCase());
  }
}

