const registerSchema = z.object({
  fullName: z.string().min(2, 'Full name must be at least 2 characters'),
  mobileNumber: z.string().min(10, 'Mobile number must be at least 10 digits').regex(/^\d+$/, 'Mobile number must contain only digits'),
  email: z.string().email('Please enter a valid email address'),
  password: z.string().min(6, 'Password must be at least 6 characters'),
  confirmPassword: z.string(),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type RegisterData = z.infer<typeof registerSchema>;

interface RegisterFormProps {
  onClose: () => void;
  onSwitchToLogin: () => void;
}

const RegisterForm: React.FC<RegisterFormProps> = ({ onClose, onSwitchToLogin }) => {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const dispatch = useDispatch();

  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
  } = useForm<RegisterData>({
    resolver: zodResolver(registerSchema),
  });

const onSubmit = async (data: RegisterData) => {
  try {
    const { data: signUpData, error: signUpError } = await supabase.auth.signUp({
      email: data.email,
      password: data.password,
    });

    if (signUpError) throw signUpError;

    const user = signUpData.user;

    // âœ… Insert into `profiles` table after signup
    const { error: profileError } = await supabase.from('profiles').insert([
      {
        id: user?.id, // same as auth user ID
        full_name: data.fullName,
        mobile_number: data.mobileNumber,
      },
    ]);

    if (profileError) throw profileError;

    // âœ… Set user data in Redux
    const userForRedux = {
      id: user?.id,
      email: data.email,
      name: data.fullName,
      mobileNumber: data.mobileNumber,
      avatar: '', // Placeholder if needed
    };

    dispatch(setUser(userForRedux));
    onClose();
  } catch (error) {
    alert(error instanceof Error ? error.message : 'Registration failed');
  }
};

  return (
