import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, Mail, Lock, User, Eye, EyeOff, ArrowRight, CheckCircle } from 'lucide-react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { useAuth } from '../../hooks/useSupabase';
import { useDispatch } from 'react-redux';
import { setUser } from '../../store/slices/authSlice';
import { isSupabaseConfigured } from '../../lib/supabase';
import { MockAuthService } from '../../utils/mockAuth';

type AuthMode = 'login' | 'register' | 'forgot-password';

interface SupabaseAuthModalProps {
  isOpen: boolean;
  onClose: () => void;
  initialMode?: AuthMode;
}

const loginSchema = z.object({
  email: z.string().email('Please enter a valid email address'),
  password: z.string().min(6, 'Password must be at least 6 characters'),
});

const registerSchema = z.object({
  fullName: z.string().min(2, 'Full name must be at least 2 characters'),
  email: z.string().email('Please enter a valid email address'),
  password: z.string().min(6, 'Password must be at least 6 characters'),
  confirmPassword: z.string(),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

const forgotPasswordSchema = z.object({
  email: z.string().email('Please enter a valid email address'),
});

type LoginData = z.infer<typeof loginSchema>;
type RegisterData = z.infer<typeof registerSchema>;
type ForgotPasswordData = z.infer<typeof forgotPasswordSchema>;

const SupabaseAuthModal: React.FC<SupabaseAuthModalProps> = ({ 
  isOpen, 
  onClose, 
  initialMode = 'login' 
}) => {
  const [mode, setMode] = useState<AuthMode>(initialMode);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const { signIn, signUp, loading } = useAuth();
  const dispatch = useDispatch();

  const loginForm = useForm<LoginData>({
    resolver: zodResolver(loginSchema),
  });

  const registerForm = useForm<RegisterData>({
    resolver: zodResolver(registerSchema),
  });

  const forgotPasswordForm = useForm<ForgotPasswordData>({
    resolver: zodResolver(forgotPasswordSchema),
  });

  const handleClose = () => {
    onClose();
    setMode('login');
    setError(null);
    setIsSubmitted(false);
    loginForm.reset();
    registerForm.reset();
    forgotPasswordForm.reset();
  };

  const onLoginSubmit = async (data: LoginData) => {
    try {
      setError(null);

      if (isSupabaseConfigured()) {
        await signIn(data.email, data.password);
      } else {
        // Use mock authentication service
        const user = await MockAuthService.login(data.email, data.password);
        const userForRedux = {
          id: user.id,
          email: user.email,
          name: user.fullName,
          avatar: user.avatar,
        };
        dispatch(setUser(userForRedux));
      }

      handleClose();
