                    </div>
                  </div>

                  {/* Limit Notice */}
                  <div className="bg-amber-50 rounded-lg p-3 mb-4">
                    <p className="text-sm text-amber-800 text-center">
                      <strong>Limit {deal.limitPerCustomer} per customer</strong> â€¢ While stocks last
                    </p>
                  </div>

                  <motion.button
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
