    name: string;
    email: string;
    contact: string;
  };
  orderId: string;
  shippingAddress?: any;
  onPaymentSuccess: (result: any) => void;
  onPaymentError: (error: string) => void;
}

const RazorpayCODPayment: React.FC<RazorpayCODPaymentProps> = ({
  amount,
  customerInfo,
  orderId,
  shippingAddress,
  onPaymentSuccess,
  onPaymentError
}) => {
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingStep, setProcessingStep] = useState('');

  const codFee = amount > 500 ? 0 : 49; // Free COD for orders above â‚¹500
  const totalAmount = amount + codFee;

  const handleCODConfirmation = async () => {
    setIsProcessing(true);
    setProcessingStep('Confirming Cash on Delivery order...');

    try {
      const result = await RazorpayService.processCODPayment(amount, customerInfo, orderId);

      if (result.success) {
