  selectedMethod, 
  onMethodChange,
  totalAmount
}) => {
  // ðŸš€ CHANGE REQUIRED: Replace with your actual UPI ID
  const ownerUPIId = 'your-upi-id@upi'; // Example: 'mybusiness@oksbi'
  // ðŸš€ CHANGE REQUIRED: Replace with your business name
  const ownerName = 'Your Business Name'; // Example: 'My Awesome Store'

  const handleMethodClick = async (methodId: StreamlinedPaymentMethodType) => {
    console.log('Payment method selected:', methodId);
    onMethodChange(methodId);

    if (methodId === 'upi') {
      const upiLink = generateUPILink(totalAmount);
      window.open(upiLink, '_blank');

      await logPaymentAttempt(methodId, totalAmount);
    }
    // ðŸš€ NOTE: For Razorpay integration, you'll need additional implementation here
    // You would typically initialize Razorpay checkout when methodId === 'razorpay-upi'
  };

  const generateUPILink = (amount: number): string => {
    // ðŸš€ OPTIONAL: You can customize the payment note (tn parameter)
    return `upi://pay?pa=${ownerUPIId}&pn=${encodeURIComponent(ownerName)}&am=${amount}&cu=INR&tn=Payment for order`;
  };

  const logPaymentAttempt = async (method: string, amount: number) => {
    try {
      const {error } = await supabase
        .from('payment_logs') // ðŸš€ CHANGE REQUIRED: Ensure this table exists in your Supabase
        .insert([
          { 
            payment_method: method, 
            amount: amount,
            status: 'initiated',
            created_at: new Date().toISOString()
            // ðŸš€ OPTIONAL: Add order_id or user_id if available
          }
        ]);

      if (error) throw error;
    } catch (err) {
      console.error('Error logging payment:', err);
      // ðŸš€ OPTIONAL: Add error handling UI feedback
    }
  };

  const paymentMethods = [
    {
      id: 'upi' as StreamlinedPaymentMethodType,
