      // ðŸš€ NOTE: This requires Razorpay account setup and proper integration
    },
    {
      id: 'cod' as StreamlinedPaymentMethodType,
      name: 'Cash on Delivery',
      description: 'Pay when you receive your order',
      icon: Truck,
      color: 'bg-green-500',
      available: true,
      // ðŸš€ NOTE: You'll need to handle COD order confirmation separately
    },
  ];

  return (
    <div className="space-y-4">
      <h3 className="text-xl font-semibold text-gray-900 mb-6">Select Payment Method</h3>

      {selectedMethod === 'upi' && (
        <div className="bg-blue-50 p-4 rounded-lg mb-4">
          <p className="text-blue-800">
            You'll be redirected to your UPI app to complete payment to {ownerName} ({ownerUPIId})
            {/* ðŸš€ OPTIONAL: Add QR code display here */}
