import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Star, ShoppingCart, Eye, Heart, Zap, Loader } from 'lucide-react';
import { useProducts, useAuth } from '../../hooks/useSupabase';
import { Product } from '../../lib/supabase';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../../store';
import { addToCart } from '../../store/slices/cartSlice';
import { addToWishlist, removeFromWishlist } from '../../store/slices/wishlistSlice';
import ProductDetail from './ProductDetail';
import CheckoutPage from '../Checkout/CheckoutPage';

interface SupabaseProductGridProps {
  category?: string;
  featured?: boolean;
  search?: string;
}

const SupabaseProductGrid: React.FC<SupabaseProductGridProps> = ({ 
  category, 
  featured, 
  search 
}) => {
  const { products, loading, error } = useProducts({ category, featured, search });
  const { isAuthenticated } = useAuth();
  const dispatch = useDispatch();
  const { items: wishlistItems } = useSelector((state: RootState) => state.wishlist);

  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [checkoutProduct, setCheckoutProduct] = useState<Product | null>(null);

  const handleAddToCart = (product: Product) => {
    // Convert Supabase product to Redux format
    const cartProduct = {
      id: product.id,
      name: product.name,
      description: product.description,
      price: product.price,
      originalPrice: product.original_price,
      category: product.category,
      images: product.images,
      rating: product.rating,
      reviewCount: product.review_count,
      inStock: product.stock > 0,
      tags: product.tags,
      featured: product.featured,
    };
    dispatch(addToCart(cartProduct));
  };

  const handleWishlistToggle = (product: Product) => {
    const wishlistProduct = {
      id: product.id,
      name: product.name,
      description: product.description,
      price: product.price,
      originalPrice: product.original_price,
      category: product.category,
      images: product.images,
      rating: product.rating,
      reviewCount: product.review_count,
      inStock: product.stock > 0,
      tags: product.tags,
      featured: product.featured,
    };

    const isInWishlist = wishlistItems.some(item => item.id === product.id);
    if (isInWishlist) {
      dispatch(removeFromWishlist(product.id));
    } else {
      dispatch(addToWishlist(wishlistProduct));
    }
  };

  const handleQuickView = (product: Product) => {
    setSelectedProduct(product);
  };

  const handleBuyNow = (product: Product) => {
    setCheckoutProduct(product);
  };

  const handleCheckoutComplete = () => {
    setCheckoutProduct(null);
    alert('Order placed successfully!');
  };

  if (checkoutProduct) {
    const checkoutProductFormatted = {
      id: checkoutProduct.id,
      name: checkoutProduct.name,
      description: checkoutProduct.description,
      price: checkoutProduct.price,
      originalPrice: checkoutProduct.original_price,
      category: checkoutProduct.category,
      images: checkoutProduct.images,
      rating: checkoutProduct.rating,
      reviewCount: checkoutProduct.review_count,
      inStock: checkoutProduct.stock > 0,
      tags: checkoutProduct.tags,
      featured: checkoutProduct.featured,
    };

    return (
      <CheckoutPage
        product={checkoutProductFormatted}
        onBack={() => setCheckoutProduct(null)}
        onComplete={handleCheckoutComplete}
      />
    );
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center py-20">
        <div className="text-center">
          <Loader className="w-12 h-12 text-yellow-600 animate-spin mx-auto mb-4" />
          <p className="text-gray-600">Loading products...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-20">
        <div className="text-red-600 mb-4">
          <p className="text-lg font-semibold">Error loading products</p>
          <p className="text-sm">{error}</p>
        </div>
        <button
          onClick={() => window.location.reload()}
          className="bg-yellow-600 hover:bg-yellow-700 text-white px-6 py-2 rounded-lg transition-colors"
        >
          Try Again
        </button>
      </div>
    );
  }

  if (products.length === 0) {
    return (
      <div className="text-center py-20">
        <p className="text-gray-600 text-lg">No products found</p>
        {search && (
          <p className="text-gray-500 text-sm mt-2">
            Try adjusting your search terms
          </p>
        )}
      </div>
    );
  }

  return (
    <>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {products.map((product, index) => {
          const isInWishlist = wishlistItems.some(item => item.id === product.id);

          return (
            <motion.div
              key={product.id}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: index * 0.1 }}
              viewport={{ once: true }}
              className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-shadow duration-300 group cursor-pointer"
              onClick={() => handleQuickView(product)}
            >
              <div className="relative overflow-hidden">
                <img
                  src={product.images[0] || '/placeholder-image.jpg'}
                  alt={product.name}
                  className="w-full h-64 object-cover group-hover:scale-105 transition-transform duration-300"
                />
                {product.original_price && (
                  <div className="absolute top-4 left-4 bg-red-500 text-white px-3 py-1 rounded-full text-sm font-semibold">
                    {Math.round(((product.original_price - product.price) / product.original_price) * 100)}% OFF
                  </div>
                )}
                {product.stock <= 5 && product.stock > 0 && (
                  <div className="absolute top-4 right-4 bg-orange-500 text-white px-2 py-1 rounded text-xs">
                    Only {product.stock} left
                  </div>
                )}
                {product.stock === 0 && (
                  <div className="absolute inset-0 bg-black/50 flex items-center justify-center">
                    <span className="text-white font-semibold">Out of Stock</span>
                  </div>
                )}
                <div className="absolute top-4 right-4 space-y-2 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                  <motion.button
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                    onClick={(e) => {
                      e.stopPropagation();
                      handleQuickView(product);
                    }}
                    className="bg-white/90 p-2 rounded-full hover:bg-white transition-colors shadow-lg"
                  >
                    <Eye className="w-5 h-5 text-gray-700" />
                  </motion.button>
                  {isAuthenticated && (
                    <motion.button
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.9 }}
                      onClick={(e) => {
                        e.stopPropagation();
                        handleWishlistToggle(product);
                      }}
                      className={`p-2 rounded-full shadow-lg transition-colors ${
                        isInWishlist
                          ? 'bg-red-500 text-white'
                          : 'bg-white/90 hover:bg-white text-gray-700'
                      }`}
                    >
                      <Heart className={`w-5 h-5 ${isInWishlist ? 'fill-current' : ''}`} />
                    </motion.button>
                  )}
                </div>
              </div>

              <div className="p-6">
                <div className="flex items-center justify-between mb-2">
                  <h3 className="text-xl font-semibold text-gray-900 line-clamp-1">{product.name}</h3>
                  <div className="flex items-center space-x-1">
                    <Star className="w-4 h-4 text-yellow-500 fill-current" />
                    <span className="text-sm text-gray-600">{product.rating}</span>
                  </div>
                </div>

                <p className="text-gray-600 mb-4 line-clamp-2">{product.description}</p>

                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center space-x-2">
                    <span className="text-2xl font-bold text-gray-900">â‚¹{product.price.toLocaleString('en-IN')}</span>
                    {product.original_price && (
                      <span className="text-lg text-gray-500 line-through">â‚¹{product.original_price.toLocaleString('en-IN')}</span>
                    )}
                  </div>
                  <span className="text-sm text-gray-500">({product.review_count} reviews)</span>
                </div>

                <div className="flex space-x-2">
                  <motion.button
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    onClick={(e) => {
                      e.stopPropagation();
                      handleAddToCart(product);
                    }}
                    disabled={product.stock === 0}
                    className="flex-1 bg-gray-900 hover:bg-gray-800 text-white py-3 rounded-xl font-semibold transition-colors flex items-center justify-center space-x-2 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <ShoppingCart className="w-4 h-4" />
                    <span>Add to Cart</span>
                  </motion.button>
                  <motion.button
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    onClick={(e) => {
                      e.stopPropagation();
                      // Navigate to streamlined checkout
                      window.dispatchEvent(new CustomEvent('navigate-to-streamlined-checkout', { 
                        detail: { product: product } 
                      }));
                    }}
                    disabled={product.stock === 0}
                    className="flex-1 bg-gradient-to-r from-yellow-600 to-yellow-700 hover:from-yellow-700 hover:to-yellow-800 text-white py-3 rounded-xl font-semibold transition-all duration-200 flex items-center justify-center space-x-2 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <Zap className="w-4 h-4" />
                    <span>Buy Now</span>
                  </motion.button>
                </div>
              </div>
            </motion.div>
          );
        })}
      </div>

      {/* Product Detail Modal */}
      {selectedProduct && (
        <ProductDetail
          product={{
            id: selectedProduct.id,
            name: selectedProduct.name,
            description: selectedProduct.description,
            price: selectedProduct.price,
            originalPrice: selectedProduct.original_price,
            category: selectedProduct.category,
            images: selectedProduct.images,
            rating: selectedProduct.rating,
            reviewCount: selectedProduct.review_count,
            inStock: selectedProduct.stock > 0,
            tags: selectedProduct.tags,
            featured: selectedProduct.featured,
          }}
          isOpen={!!selectedProduct}
          onClose={() => setSelectedProduct(null)}
        />
      )}
    </>
  );
};

export default SupabaseProductGrid;

